package assignmentJava;

public class calculatePaw {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int number = 2, p = 5 ;
		int result =   1;   // 2*1
				// looping condition
		for ( int i = p; i !=0; ) {
			result	*= number ;
		}
		
		System.out.println(number +" ^ " +  p+ " = " +  result);
				
		

	}

}
