public class Question3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
                int x = 101 ; 
                int y = 202;
                System.out.println("Before Swapping X and Y : " + "\n x : " + x + " , " + "  y : " + y );
                

                x = x - y ; 
                y = x + y ;
                x = y - x ; 
                System.out.println("After  Swapping X and Y : " + "\n x : " + x + " , " + "  y : " + y);
                
		
		
		
		
		
		
		
		
		
				

	}

}
